        //     <div className="cart-item">
        //       {Object.keys(item).map((key) => (
        //         <div key={key}>
        //           <p>{item[key].name}</p>
        //           <p>{item[key].price}</p>
        //           <p>{item[key].amount}</p>
        //         </div>
        //       ))}
        //     </div>
        //   ))}
        // </div>
      );
  }

  export default Cart;
